mapper.select:
select authorities
from tb_role_authority
where role = #{id,jdbcType=VARCHAR}

select
FROM tb_role t where 1=1
and id = #{id}

mapper.insert:
insert into tb_role (id, create_date, modify_date,
description, is_system, name
)
values (#{id,jdbcType=BIGINT}, now(), now(),
#{description,jdbcType=VARCHAR}, #{isSystem,jdbcType=BIT},
#{name,jdbcType=VARCHAR}
)

mapper.update:
update tb_role
set
modify_date = now(),
description = #{description,jdbcType=VARCHAR},
name = #{name,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}
